import { NextRequest, NextResponse } from 'next/server';

import { trelloSyncService } from '@/lib/services/trello-sync';

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();

    // Validate webhook payload
    if (!body.action || !body.model) {
      return NextResponse.json(
        { error: 'Invalid webhook payload' },
        { status: 400 }
      );
    }

    // Log webhook data for debugging in development only
    if (process.env.NODE_ENV === 'development') {
      console.log('Trello webhook received:', {
        action: body.action.type,
        card: body.action.data?.card?.name,
        list: body.action.data?.list?.name,
        member: body.action.memberCreator?.fullName,
      });
    }

    // Filter relevant actions
    const relevantActions = [
      'createCard',
      'updateCard',
      'deleteCard',
      'moveCardFromBoard',
      'moveCardToBoard',
      'addChecklistToCard',
      'updateCheckItemStateOnCard',
    ];

    if (relevantActions.includes(body.action.type)) {
      // Trigger sync in background
      setTimeout(async () => {
        try {
          await trelloSyncService.syncFromTrello();
          // Sync completed successfully
        } catch {
          // Handle sync errors silently in production
        }
      }, 1000); // Small delay to ensure Trello data is consistent
    }

    return NextResponse.json({ success: true });
  } catch (error) {
    // Log errors only in development
    if (process.env.NODE_ENV === 'development') {
      console.error('Webhook processing error:', error);
    }

    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

export async function GET(_request: NextRequest) {
  // Handle Trello webhook verification
  return NextResponse.json({ message: 'Trello webhook endpoint active' });
}

'use client';

import { useEffect } from 'react';

export default function Error({
  error,
  reset,
}: {
  error: Error;
  reset: () => void;
}) {
  useEffect(() => {
    // Log the error to an error reporting service in development only
    if (process.env.NODE_ENV === 'development') {
      console.error(error);
    }
  }, [error]);

  return (
    <div>
      <h2>Something went wrong!</h2>
      <button
        onClick={
          // Attempt to recover by trying to re-render the segment
          () => reset()
        }
      >
        Try again
      </button>
    </div>
  );
}

'use client';

import { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { FiRefreshCw, FiTrendingUp, FiUsers, FiActivity } from 'react-icons/fi';
import { Button } from '@heroui/button';
import { Toaster } from 'react-hot-toast';

import { Project } from '@/types/project';
import { ProjectGroup } from '@/components/project-group';
import { ProjectFilters } from '@/components/project-filters';
import { ProjectDetailModal } from '@/components/project-detail-modal';
import {
  ErrorBoundary,
  ProjectErrorFallback,
} from '@/components/error-boundary';
import { TrelloSetupGuide } from '@/components/trello-setup-guide';
import { RealTimeSyncToggle } from '@/components/real-time-sync-toggle';
import {
  ProjectGridSkeleton,
  StatCardSkeleton,
} from '@/components/loading-skeleton';
import { title, subtitle } from '@/components/primitives';
import { useProjectStore } from '@/lib/store';

export default function Home() {
  const [selectedProject, setSelectedProject] = useState<Project | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const {
    loading,
    error,
    lastUpdated,
    fetchProjects,
    refreshProjects,
    getFilteredProjects,
    getGroupedProjects,
    getProjectStats,
    startRealTimeSync: _startRealTimeSync,
    stopRealTimeSync,
    isRealTimeSyncActive,
  } = useProjectStore();

  const filteredProjects = getFilteredProjects();
  const groupedProjects = getGroupedProjects();
  const stats = getProjectStats();

  useEffect(() => {
    fetchProjects();

    // Cleanup real-time sync on unmount
    return () => {
      if (isRealTimeSyncActive()) {
        stopRealTimeSync();
      }
    };
  }, [fetchProjects, isRealTimeSyncActive, stopRealTimeSync]);

  const handleRefresh = async () => {
    await refreshProjects();
  };

  const handleProjectClick = (project: Project) => {
    setSelectedProject(project);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedProject(null);
  };

  // Show Trello setup guide if credentials are missing
  if (error && error.includes('Trello API credentials not configured')) {
    return (
      <ErrorBoundary fallback={ProjectErrorFallback}>
        <div className="min-h-screen py-8">
          <Toaster position="top-right" />
          <TrelloSetupGuide />
        </div>
      </ErrorBoundary>
    );
  }

  if (error) {
    return (
      <ProjectErrorFallback error={new Error(error)} reset={handleRefresh} />
    );
  }

  return (
    <ErrorBoundary fallback={ProjectErrorFallback}>
      <div className="min-h-screen py-8">
        <Toaster position="top-right" />

        {/* Header */}
        <motion.div
          animate={{ opacity: 1, y: 0 }}
          className="text-center mb-12"
          initial={{ opacity: 0, y: -20 }}
        >
          <h1 className={`${title({ size: 'lg' })} mb-4`}>
            <span className="text-primary-600">inPatch</span> Suporte
          </h1>
          <p className={`${subtitle()} max-w-2xl mx-auto`}>
            Acompanhe o desenvolvimento dos principais projetos e automa√ß√µes do
            time de Suporte da inChurch
          </p>
        </motion.div>

        {/* Stats Cards */}
        <motion.div
          animate={{ opacity: 1, y: 0 }}
          className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8"
          initial={{ opacity: 0, y: 20 }}
          transition={{ delay: 0.2 }}
        >
          {loading ? (
            Array.from({ length: 4 }).map((_, index) => (
              <StatCardSkeleton key={index} />
            ))
          ) : (
            <>
              <div className="liquid-glass p-4 text-center hover:scale-105 transition-transform duration-200">
                <FiActivity className="w-8 h-8 mx-auto mb-2 text-primary-600" />
                <div className="text-2xl font-bold text-foreground">
                  {stats.total}
                </div>
                <div className="text-sm text-default-600">Total Projetos</div>
              </div>
              <div className="liquid-glass p-4 text-center hover:scale-105 transition-transform duration-200">
                <FiTrendingUp className="w-8 h-8 mx-auto mb-2 text-orange-500" />
                <div className="text-2xl font-bold text-foreground">
                  {stats.inProgress}
                </div>
                <div className="text-sm text-default-600">Em Andamento</div>
              </div>
              <div className="liquid-glass p-4 text-center hover:scale-105 transition-transform duration-200">
                <FiUsers className="w-8 h-8 mx-auto mb-2 text-green-500" />
                <div className="text-2xl font-bold text-foreground">
                  {stats.completed}
                </div>
                <div className="text-sm text-default-600">Conclu√≠dos</div>
              </div>
              <div className="liquid-glass p-4 text-center hover:scale-105 transition-transform duration-200">
                <div className="w-8 h-8 mx-auto mb-2 rounded-full bg-gradient-to-r from-primary-500 to-primary-600 flex items-center justify-center text-white font-bold shadow-lg">
                  {stats.avgProgress}%
                </div>
                <div className="text-2xl font-bold text-foreground">
                  {stats.avgProgress}%
                </div>
                <div className="text-sm text-default-600">Progresso M√©dio</div>
              </div>
            </>
          )}
        </motion.div>

        {/* Filters */}
        <motion.div
          animate={{ opacity: 1 }}
          className="mb-8"
          initial={{ opacity: 0 }}
          transition={{ delay: 0.4 }}
        >
          <ProjectFilters />
        </motion.div>

        {/* Real-time Sync Toggle */}
        <motion.div
          animate={{ opacity: 1 }}
          className="mb-6"
          initial={{ opacity: 0 }}
          transition={{ delay: 0.5 }}
        >
          <RealTimeSyncToggle />
        </motion.div>

        {/* Controls */}
        <motion.div
          animate={{ opacity: 1 }}
          className="flex flex-col sm:flex-row justify-between items-center mb-8 gap-4"
          initial={{ opacity: 0 }}
          transition={{ delay: 0.6 }}
        >
          <div className="text-sm text-default-600 order-2 sm:order-1">
            {lastUpdated && `√öltima atualiza√ß√£o: ${lastUpdated}`}
          </div>
          <Button
            className="order-1 sm:order-2"
            color="primary"
            isDisabled={loading}
            startContent={
              <FiRefreshCw
                className={`w-4 h-4 ${loading ? 'animate-spin' : ''}`}
              />
            }
            variant="ghost"
            onClick={handleRefresh}
          >
            {loading ? 'Atualizando...' : 'Atualizar'}
          </Button>
        </motion.div>

        {/* Projects Grouped by Status */}
        {loading ? (
          <ProjectGridSkeleton count={6} />
        ) : (
          <motion.div
            animate={{ opacity: 1 }}
            initial={{ opacity: 0 }}
            transition={{ delay: 0.7 }}
          >
            {/* Em Andamento - Always first */}
            <ProjectGroup
              projects={groupedProjects['em-andamento']}
              status="em-andamento"
              onProjectClick={handleProjectClick}
            />

            {/* A Fazer - Second */}
            <ProjectGroup
              projects={groupedProjects['a-fazer']}
              status="a-fazer"
              onProjectClick={handleProjectClick}
            />

            {/* Conclu√≠do - Last */}
            <ProjectGroup
              projects={groupedProjects['concluido']}
              status="concluido"
              onProjectClick={handleProjectClick}
            />
          </motion.div>
        )}

        {/* Empty State */}
        {!loading && filteredProjects.length === 0 && (
          <motion.div
            animate={{ opacity: 1 }}
            className="text-center py-16"
            initial={{ opacity: 0 }}
          >
            <div className="text-6xl mb-4">üìã</div>
            <h3 className="text-xl font-semibold text-foreground mb-2">
              Nenhum projeto encontrado
            </h3>
            <p className="text-default-600 mb-4">
              N√£o h√° projetos dispon√≠veis no momento.
            </p>
            <Button color="primary" onClick={handleRefresh}>
              Tentar novamente
            </Button>
          </motion.div>
        )}

        {/* Project Detail Modal */}
        <ProjectDetailModal
          isOpen={isModalOpen}
          project={selectedProject}
          onClose={handleCloseModal}
        />
      </div>
    </ErrorBoundary>
  );
}

'use client';

import React from 'react';
import { Button } from '@heroui/button';
import { FiAlertTriangle, FiRefreshCw } from 'react-icons/fi';

interface ErrorBoundaryState {
  hasError: boolean;
  error?: Error;
}

interface ErrorBoundaryProps {
  children: React.ReactNode;
  fallback?: React.ComponentType<{ error?: Error; reset: () => void }>;
}

export class ErrorBoundary extends React.Component<
  ErrorBoundaryProps,
  ErrorBoundaryState
> {
  constructor(props: ErrorBoundaryProps) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error: Error): ErrorBoundaryState {
    return { hasError: true, error };
  }

  componentDidCatch(error: Error, errorInfo: React.ErrorInfo) {
    // Log errors only in development
    if (process.env.NODE_ENV === 'development') {
      console.error('Error caught by boundary:', error, errorInfo);
    }
  }

  render() {
    if (this.state.hasError) {
      const reset = () => {
        this.setState({ hasError: false, error: undefined });
      };

      if (this.props.fallback) {
        const FallbackComponent = this.props.fallback;

        return <FallbackComponent error={this.state.error} reset={reset} />;
      }

      return <DefaultErrorFallback error={this.state.error} reset={reset} />;
    }

    return this.props.children;
  }
}

interface ErrorFallbackProps {
  error?: Error;
  reset: () => void;
}

export const DefaultErrorFallback: React.FC<ErrorFallbackProps> = ({
  error,
  reset,
}) => (
  <div className="min-h-screen flex items-center justify-center p-4">
    <div className="liquid-glass p-8 max-w-md w-full text-center">
      <FiAlertTriangle className="w-16 h-16 mx-auto mb-4 text-red-500" />
      <h2 className="text-xl font-bold text-foreground mb-2">
        Algo deu errado
      </h2>
      <p className="text-default-600 mb-4">
        {error?.message || 'Ocorreu um erro inesperado. Tente novamente.'}
      </p>
      <Button
        color="primary"
        startContent={<FiRefreshCw className="w-4 h-4" />}
        onClick={reset}
      >
        Tentar Novamente
      </Button>
    </div>
  </div>
);

export const ProjectErrorFallback: React.FC<ErrorFallbackProps> = ({
  error,
  reset,
}) => (
  <div className="liquid-glass p-6 text-center">
    <FiAlertTriangle className="w-12 h-12 mx-auto mb-3 text-red-500" />
    <h3 className="text-lg font-semibold text-foreground mb-2">
      Erro ao carregar projetos
    </h3>
    <p className="text-default-600 text-sm mb-4">
      {error?.message || 'N√£o foi poss√≠vel carregar os projetos.'}
    </p>
    <Button
      color="primary"
      size="sm"
      startContent={<FiRefreshCw className="w-4 h-4" />}
      variant="ghost"
      onClick={reset}
    >
      Tentar Novamente
    </Button>
  </div>
);

import { cacheService } from '../cache/cache-service';
import {
  advancedRateLimiter,
  enhancedErrorHandler,
} from '../utils/advanced-rate-limiter';

import { enhancedTrelloApi } from './trello-enhanced';
import { webhookHandler } from './webhook-handler';

import { Project } from '@/types/project';

interface TestResult {
  testName: string;
  success: boolean;
  duration: number;
  error?: string;
  details?: any;
}

interface TestSuite {
  suiteName: string;
  results: TestResult[];
  totalTests: number;
  passedTests: number;
  failedTests: number;
  totalDuration: number;
}

/**
 * Comprehensive test suite for Enhanced Trello API integration
 * Tests all Phase 3 improvements and validates functionality
 */
export class EnhancedTrelloIntegrationTests {
  private testResults: TestSuite[] = [];

  /**
   * Run all integration tests
   */
  async runAllTests(): Promise<{
    success: boolean;
    totalSuites: number;
    totalTests: number;
    passedTests: number;
    failedTests: number;
    duration: number;
    suites: TestSuite[];
  }> {
    console.log('üöÄ Starting Enhanced Trello API Integration Tests - Fase 3');
    const startTime = Date.now();

    this.testResults = [];

    // Run test suites
    await this.testEnhancedTrelloAPI();
    await this.testCacheService();
    await this.testAdvancedRateLimiter();
    await this.testWebhookHandler();
    await this.testErrorHandling();
    await this.testPerformanceMetrics();

    const totalDuration = Date.now() - startTime;
    const totalTests = this.testResults.reduce(
      (sum, suite) => sum + suite.totalTests,
      0
    );
    const passedTests = this.testResults.reduce(
      (sum, suite) => sum + suite.passedTests,
      0
    );
    const failedTests = this.testResults.reduce(
      (sum, suite) => sum + suite.failedTests,
      0
    );

    const summary = {
      success: failedTests === 0,
      totalSuites: this.testResults.length,
      totalTests,
      passedTests,
      failedTests,
      duration: totalDuration,
      suites: this.testResults,
    };

    this.printTestSummary(summary);

    return summary;
  }

  /**
   * Test Enhanced Trello API functionality
   */
  private async testEnhancedTrelloAPI(): Promise<void> {
    const suite: TestSuite = {
      suiteName: 'Enhanced Trello API',
      results: [],
      totalTests: 0,
      passedTests: 0,
      failedTests: 0,
      totalDuration: 0,
    };

    // Test 1: Health Check
    await this.runTest(suite, 'Health Check', async () => {
      const health = await enhancedTrelloApi.healthCheck();

      if (!health || !health.status) {
        throw new Error('Health check returned invalid response');
      }

      return { status: health.status, metrics: health.metrics };
    });

    // Test 2: Get Optimized Board Cards
    await this.runTest(suite, 'Get Optimized Board Cards', async () => {
      const cards = await enhancedTrelloApi.getOptimizedBoardCards();

      if (!Array.isArray(cards)) {
        throw new Error('Expected array of cards');
      }

      return { cardCount: cards.length };
    });

    // Test 3: Get Cached Board Cards
    await this.runTest(suite, 'Get Cached Board Cards', async () => {
      const cards = await enhancedTrelloApi.getCachedBoardCards();

      if (!Array.isArray(cards)) {
        throw new Error('Expected array of cards');
      }

      return { cardCount: cards.length, cached: true };
    });

    // Test 4: Transform Cards to Projects Enhanced
    await this.runTest(suite, 'Transform Cards Enhanced', async () => {
      const cards = await enhancedTrelloApi.getOptimizedBoardCards();
      const projects = enhancedTrelloApi.transformCardsToProjectsEnhanced(
        cards.slice(0, 5)
      );

      if (!Array.isArray(projects)) {
        throw new Error('Expected array of projects');
      }

      // Validate project structure
      projects.forEach(project => {
        if (!project.id || !project.title || !project.status) {
          throw new Error('Invalid project structure');
        }
      });

      return {
        projectCount: projects.length,
        sampleProject: projects[0]?.title,
      };
    });

    // Test 5: Batch Operations (if we have multiple cards)
    await this.runTest(suite, 'Batch Card Operations', async () => {
      const cards = await enhancedTrelloApi.getOptimizedBoardCards();
      const cardIds = cards.slice(0, 3).map(card => card.id);

      if (cardIds.length > 0) {
        const batchCards = await enhancedTrelloApi.getBatchCards(cardIds);

        if (!Array.isArray(batchCards)) {
          throw new Error('Expected array of batch cards');
        }

        return {
          requestedCards: cardIds.length,
          receivedCards: batchCards.length,
        };
      }

      return { message: 'No cards available for batch testing' };
    });

    // Test 6: Circuit Breaker Status
    await this.runTest(suite, 'Circuit Breaker Status', async () => {
      const status = enhancedTrelloApi.getCircuitBreakerStatus();

      if (!status || typeof status.state !== 'string') {
        throw new Error('Invalid circuit breaker status');
      }

      return status;
    });

    // Test 7: API Metrics
    await this.runTest(suite, 'API Metrics', async () => {
      const metrics = enhancedTrelloApi.getMetrics();

      if (!metrics || typeof metrics.totalRequests !== 'number') {
        throw new Error('Invalid metrics response');
      }

      return metrics;
    });

    this.testResults.push(suite);
  }

  /**
   * Test Cache Service functionality
   */
  private async testCacheService(): Promise<void> {
    const suite: TestSuite = {
      suiteName: 'Cache Service',
      results: [],
      totalTests: 0,
      passedTests: 0,
      failedTests: 0,
      totalDuration: 0,
    };

    // Test 1: Cache Health Check
    await this.runTest(suite, 'Cache Health Check', async () => {
      const isHealthy = cacheService.isHealthy();

      return { healthy: isHealthy };
    });

    // Test 2: Cache Statistics
    await this.runTest(suite, 'Cache Statistics', async () => {
      const stats = cacheService.getStats();

      if (!stats || typeof stats.totalEntries !== 'number') {
        throw new Error('Invalid cache stats');
      }

      return stats;
    });

    // Test 3: Set and Get Cached Projects
    await this.runTest(suite, 'Set and Get Cached Projects', async () => {
      const mockProjects: Project[] = [
        {
          id: 'test-1',
          title: 'Test Project',
          description: 'Test Description',
          progress: 50,
          platforms: ['N8N'],
          responsible: ['Guilherme Souza'],
          startDate: new Date().toISOString(),
          estimatedEndDate: new Date(Date.now() + 86400000).toISOString(),
          status: 'em-andamento',
          priority: 'medium',
          trelloCardId: 'test-card-1',
          labels: ['test'],
          createdAt: new Date().toISOString(),
          updatedAt: new Date().toISOString(),
        },
      ];

      await cacheService.setCachedProjects(mockProjects);
      const cachedProjects = await cacheService.getCachedProjects();

      if (!cachedProjects || cachedProjects.length !== 1) {
        throw new Error('Cache set/get failed');
      }

      return { cached: cachedProjects.length, original: mockProjects.length };
    });

    // Test 4: Cache Invalidation
    await this.runTest(suite, 'Cache Invalidation', async () => {
      await cacheService.invalidateProjects();
      const cachedProjects = await cacheService.getCachedProjects();

      if (cachedProjects !== null) {
        throw new Error('Cache invalidation failed');
      }

      return { invalidated: true };
    });

    // Test 5: Cache Configuration
    await this.runTest(suite, 'Cache Configuration', async () => {
      const config = cacheService.getConfig();

      if (!config || typeof config.maxSize !== 'number') {
        throw new Error('Invalid cache configuration');
      }

      return config;
    });

    this.testResults.push(suite);
  }

  /**
   * Test Advanced Rate Limiter
   */
  private async testAdvancedRateLimiter(): Promise<void> {
    const suite: TestSuite = {
      suiteName: 'Advanced Rate Limiter',
      results: [],
      totalTests: 0,
      passedTests: 0,
      failedTests: 0,
      totalDuration: 0,
    };

    // Test 1: Rate Limit Check
    await this.runTest(suite, 'Rate Limit Check', async () => {
      const testKey = 'test-endpoint';
      const allowed = advancedRateLimiter.isAllowed(testKey);

      return { allowed, key: testKey };
    });

    // Test 2: Rate Limit Status
    await this.runTest(suite, 'Rate Limit Status', async () => {
      const testKey = 'test-endpoint';
      const status = advancedRateLimiter.getStatus(testKey);

      if (!status || typeof status.allowed !== 'boolean') {
        throw new Error('Invalid rate limit status');
      }

      return status;
    });

    // Test 3: Rate Limit Statistics
    await this.runTest(suite, 'Rate Limit Statistics', async () => {
      const stats = advancedRateLimiter.getStats();

      if (!stats || typeof stats.totalKeys !== 'number') {
        throw new Error('Invalid rate limit stats');
      }

      return stats;
    });

    // Test 4: Multiple Requests
    await this.runTest(suite, 'Multiple Requests Handling', async () => {
      const testKey = 'burst-test';
      let allowedCount = 0;

      // Try 5 requests quickly
      for (let i = 0; i < 5; i++) {
        if (advancedRateLimiter.isAllowed(testKey)) {
          allowedCount++;
        }
      }

      return { totalRequests: 5, allowedRequests: allowedCount };
    });

    this.testResults.push(suite);
  }

  /**
   * Test Webhook Handler
   */
  private async testWebhookHandler(): Promise<void> {
    const suite: TestSuite = {
      suiteName: 'Webhook Handler',
      results: [],
      totalTests: 0,
      passedTests: 0,
      failedTests: 0,
      totalDuration: 0,
    };

    // Test 1: Webhook Health Status
    await this.runTest(suite, 'Webhook Health Status', async () => {
      const health = webhookHandler.getHealthStatus();

      if (!health || !health.status) {
        throw new Error('Invalid webhook health status');
      }

      return health;
    });

    // Test 2: Webhook Statistics
    await this.runTest(suite, 'Webhook Statistics', async () => {
      const stats = webhookHandler.getStats();

      if (!stats || typeof stats.totalProcessed !== 'number') {
        throw new Error('Invalid webhook stats');
      }

      return stats;
    });

    // Test 3: Mock Webhook Event Processing
    await this.runTest(suite, 'Mock Webhook Event Processing', async () => {
      const mockEvent = {
        action: {
          id: 'test-action-1',
          type: 'updateCard',
          date: new Date().toISOString(),
          memberCreator: {
            id: 'test-member',
            fullName: 'Test User',
            username: 'testuser',
          },
        },
        model: {
          id: 'test-model',
          name: 'Test Model',
        },
        data: {
          card: {
            id: 'test-card-1',
            name: 'Test Card',
            desc: 'Test Description',
          },
          old: {
            name: 'Old Test Card',
          },
        },
      };

      const result = await webhookHandler.processWebhookEvent(mockEvent);

      if (!result || typeof result.success !== 'boolean') {
        throw new Error('Invalid webhook processing result');
      }

      return result;
    });

    // Test 4: Webhook Subscription
    await this.runTest(suite, 'Webhook Subscription', async () => {
      let callbackCalled = false;

      const unsubscribe = webhookHandler.subscribe(projects => {
        callbackCalled = true;
      });

      // Test unsubscribe
      unsubscribe();

      return { subscriptionTest: true, callbackRegistered: !callbackCalled };
    });

    this.testResults.push(suite);
  }

  /**
   * Test Error Handling
   */
  private async testErrorHandling(): Promise<void> {
    const suite: TestSuite = {
      suiteName: 'Error Handling',
      results: [],
      totalTests: 0,
      passedTests: 0,
      failedTests: 0,
      totalDuration: 0,
    };

    // Test 1: Error Categorization
    await this.runTest(suite, 'Error Categorization', async () => {
      const testErrors = [
        { status: 401, message: 'Unauthorized' },
        { status: 429, message: 'Rate Limited' },
        { status: 500, message: 'Server Error' },
        { message: 'Network Error' },
      ];

      const results = testErrors.map(error => {
        const category = enhancedErrorHandler.categorizeError(error);

        return {
          error: error.status || error.message,
          category: category.category,
        };
      });

      return { categorizedErrors: results };
    });

    // Test 2: Error Statistics
    await this.runTest(suite, 'Error Statistics', async () => {
      const stats = enhancedErrorHandler.getErrorStats();

      if (!stats || typeof stats.totalErrors !== 'number') {
        throw new Error('Invalid error stats');
      }

      return stats;
    });

    // Test 3: Endpoint Health Check
    await this.runTest(suite, 'Endpoint Health Check', async () => {
      const testEndpoint = 'test-endpoint';
      const isHealthy = enhancedErrorHandler.isEndpointHealthy(testEndpoint);

      return { endpoint: testEndpoint, healthy: isHealthy };
    });

    this.testResults.push(suite);
  }

  /**
   * Test Performance Metrics
   */
  private async testPerformanceMetrics(): Promise<void> {
    const suite: TestSuite = {
      suiteName: 'Performance Metrics',
      results: [],
      totalTests: 0,
      passedTests: 0,
      failedTests: 0,
      totalDuration: 0,
    };

    // Test 1: API Response Time
    await this.runTest(suite, 'API Response Time', async () => {
      const startTime = Date.now();

      try {
        await enhancedTrelloApi.getOptimizedBoardCards(['id', 'name']);
        const responseTime = Date.now() - startTime;

        return {
          responseTime,
          acceptable: responseTime < 5000,
          fast: responseTime < 1000,
        };
      } catch (error) {
        const responseTime = Date.now() - startTime;

        return {
          responseTime,
          error: true,
          message: error instanceof Error ? error.message : 'Unknown error',
        };
      }
    });

    // Test 2: Cache Performance
    await this.runTest(suite, 'Cache Performance', async () => {
      const iterations = 100;
      const testData = Array.from({ length: 10 }, (_, i) => ({
        id: `perf-test-${i}`,
        title: `Performance Test ${i}`,
        description: 'Performance test project',
        progress: Math.floor(Math.random() * 100),
        platforms: ['N8N'],
        responsible: ['Guilherme Souza'],
        startDate: new Date().toISOString(),
        estimatedEndDate: new Date(Date.now() + 86400000).toISOString(),
        status: 'em-andamento' as const,
        priority: 'medium' as const,
        trelloCardId: `perf-card-${i}`,
        labels: ['performance'],
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString(),
      }));

      // Test cache write performance
      const writeStartTime = Date.now();

      for (let i = 0; i < iterations; i++) {
        await cacheService.setCachedProjects(testData);
      }
      const writeTime = Date.now() - writeStartTime;

      // Test cache read performance
      const readStartTime = Date.now();

      for (let i = 0; i < iterations; i++) {
        await cacheService.getCachedProjects();
      }
      const readTime = Date.now() - readStartTime;

      return {
        iterations,
        writeTime,
        readTime,
        avgWriteTime: writeTime / iterations,
        avgReadTime: readTime / iterations,
      };
    });

    // Test 3: Memory Usage Estimation
    await this.runTest(suite, 'Memory Usage Estimation', async () => {
      const stats = cacheService.getStats();
      const memoryUsage = stats.memoryUsage;

      return {
        memoryUsage,
        memoryUsageMB: Math.round((memoryUsage / (1024 * 1024)) * 100) / 100,
        acceptable: memoryUsage < 50 * 1024 * 1024, // 50MB
      };
    });

    this.testResults.push(suite);
  }

  /**
   * Run individual test
   */
  private async runTest(
    suite: TestSuite,
    testName: string,
    testFunction: () => Promise<any>
  ): Promise<void> {
    const startTime = Date.now();

    suite.totalTests++;

    try {
      const result = await testFunction();
      const duration = Date.now() - startTime;

      suite.results.push({
        testName,
        success: true,
        duration,
        details: result,
      });

      suite.passedTests++;
      suite.totalDuration += duration;

      console.log(`‚úÖ ${testName} - ${duration}ms`);
    } catch (error) {
      const duration = Date.now() - startTime;

      suite.results.push({
        testName,
        success: false,
        duration,
        error: error instanceof Error ? error.message : 'Unknown error',
      });

      suite.failedTests++;
      suite.totalDuration += duration;

      console.log(
        `‚ùå ${testName} - ${duration}ms - ${error instanceof Error ? error.message : 'Unknown error'}`
      );
    }
  }

  /**
   * Print comprehensive test summary
   */
  private printTestSummary(summary: any): void {
    console.log('\n' + '='.repeat(80));
    console.log('üß™ ENHANCED TRELLO API INTEGRATION TESTS - FASE 3 SUMMARY');
    console.log('='.repeat(80));

    console.log(`\nüìä Overall Results:`);
    console.log(`   Total Test Suites: ${summary.totalSuites}`);
    console.log(`   Total Tests: ${summary.totalTests}`);
    console.log(`   ‚úÖ Passed: ${summary.passedTests}`);
    console.log(`   ‚ùå Failed: ${summary.failedTests}`);
    console.log(`   ‚è±Ô∏è  Total Duration: ${summary.duration}ms`);
    console.log(
      `   üìà Success Rate: ${((summary.passedTests / summary.totalTests) * 100).toFixed(1)}%`
    );

    console.log(`\nüìã Test Suite Details:`);
    summary.suites.forEach((suite: TestSuite) => {
      const successRate =
        suite.totalTests > 0 ? (suite.passedTests / suite.totalTests) * 100 : 0;

      console.log(`\n   ${suite.suiteName}:`);
      console.log(
        `     Tests: ${suite.passedTests}/${suite.totalTests} passed (${successRate.toFixed(1)}%)`
      );
      console.log(`     Duration: ${suite.totalDuration}ms`);

      if (suite.failedTests > 0) {
        console.log(`     Failed Tests:`);
        suite.results
          .filter(result => !result.success)
          .forEach(result => {
            console.log(`       - ${result.testName}: ${result.error}`);
          });
      }
    });

    if (summary.success) {
      console.log(
        `\nüéâ ALL TESTS PASSED! Enhanced Trello API integration is working correctly.`
      );
    } else {
      console.log(`\n‚ö†Ô∏è  Some tests failed. Please review the errors above.`);
    }

    console.log('\n' + '='.repeat(80));
  }

  /**
   * Generate test report
   */
  generateReport(): string {
    const report = {
      timestamp: new Date().toISOString(),
      phase: 'Fase 3 - API Trello Enhancement',
      testResults: this.testResults,
      summary: {
        totalSuites: this.testResults.length,
        totalTests: this.testResults.reduce(
          (sum, suite) => sum + suite.totalTests,
          0
        ),
        passedTests: this.testResults.reduce(
          (sum, suite) => sum + suite.passedTests,
          0
        ),
        failedTests: this.testResults.reduce(
          (sum, suite) => sum + suite.failedTests,
          0
        ),
        totalDuration: this.testResults.reduce(
          (sum, suite) => sum + suite.totalDuration,
          0
        ),
      },
    };

    return JSON.stringify(report, null, 2);
  }
}

// Export test runner
export const enhancedTrelloTests = new EnhancedTrelloIntegrationTests();

import { Project, Platform, TeamMember } from '@/types/project';
import { TrelloAPI } from '@/lib/trello';
import { getAPIConfig } from '@/lib/config/api';
import { APIError, sanitizeString } from '@/lib/utils/validation';

interface TrelloCard {
  id: string;
  name: string;
  desc: string;
  due: string | null;
  dateLastActivity: string;
  list: {
    id: string;
    name: string;
  };
  labels: Array<{
    id: string;
    name: string;
    color: string;
  }>;
  members: Array<{
    id: string;
    fullName: string;
    username: string;
  }>;
  badges: {
    checkItems: number;
    checkItemsChecked: number;
  };
}

interface WebhookConfig {
  modelType: 'board' | 'list' | 'card';
  events: string[];
  description?: string;
}

interface BatchOperation<T> {
  id: string;
  operation: () => Promise<T>;
  retries: number;
  priority: 'high' | 'medium' | 'low';
}

interface CircuitBreakerState {
  failures: number;
  lastFailureTime: number;
  state: 'closed' | 'open' | 'half-open';
}

interface APIMetrics {
  totalRequests: number;
  successfulRequests: number;
  failedRequests: number;
  averageResponseTime: number;
  lastRequestTime: number;
}

/**
 * Enhanced Trello API with advanced features:
 * - Batch operations for multiple cards
 * - Advanced webhook management
 * - Circuit breaker pattern
 * - Retry with exponential backoff
 * - Performance metrics
 * - Connection pooling simulation
 */
export class EnhancedTrelloAPI extends TrelloAPI {
  private batchQueue: Map<string, BatchOperation<any>> = new Map();
  private circuitBreaker: CircuitBreakerState = {
    failures: 0,
    lastFailureTime: 0,
    state: 'closed',
  };
  private metrics: APIMetrics = {
    totalRequests: 0,
    successfulRequests: 0,
    failedRequests: 0,
    averageResponseTime: 0,
    lastRequestTime: 0,
  };
  private activeWebhooks: Map<string, any> = new Map();
  private connectionPool: Set<string> = new Set();

  // Circuit breaker configuration
  private readonly CIRCUIT_BREAKER_THRESHOLD = 5;
  private readonly CIRCUIT_BREAKER_TIMEOUT = 60000; // 1 minute
  private readonly MAX_BATCH_SIZE = 10;
  private readonly RETRY_DELAYS = [1000, 2000, 4000, 8000, 16000]; // Exponential backoff

  constructor() {
    super();
    this.initializeConnectionPool();
  }

  /**
   * Initialize connection pool simulation
   */
  private initializeConnectionPool(): void {
    // Simulate connection pool with 5 connections
    for (let i = 0; i < 5; i++) {
      this.connectionPool.add(`conn-${i}`);
    }
  }

  /**
   * Enhanced request method with circuit breaker and metrics
   */
  private async makeEnhancedRequest(
    endpoint: string,
    options: Record<string, any> = {},
    retryCount: number = 0
  ): Promise<any> {
    // Check circuit breaker
    if (this.circuitBreaker.state === 'open') {
      const timeSinceLastFailure =
        Date.now() - this.circuitBreaker.lastFailureTime;

      if (timeSinceLastFailure < this.CIRCUIT_BREAKER_TIMEOUT) {
        throw new APIError(
          'Circuit breaker is open. Service temporarily unavailable.',
          503,
          endpoint
        );
      } else {
        this.circuitBreaker.state = 'half-open';
      }
    }

    const startTime = Date.now();

    this.metrics.totalRequests++;

    try {
      // Use parent's makeRequest method
      const result = await (this as any).makeRequest(endpoint, options);

      // Update metrics on success
      const responseTime = Date.now() - startTime;

      this.updateMetrics(true, responseTime);
      this.resetCircuitBreaker();

      return result;
    } catch (error) {
      const responseTime = Date.now() - startTime;

      this.updateMetrics(false, responseTime);
      this.handleCircuitBreakerFailure();

      // Retry with exponential backoff
      if (retryCount < this.RETRY_DELAYS.length && this.shouldRetry(error)) {
        const delay = this.RETRY_DELAYS[retryCount];

        await this.sleep(delay);

        return this.makeEnhancedRequest(endpoint, options, retryCount + 1);
      }

      throw error;
    }
  }

  /**
   * Update API metrics
   */
  private updateMetrics(success: boolean, responseTime: number): void {
    if (success) {
      this.metrics.successfulRequests++;
    } else {
      this.metrics.failedRequests++;
    }

    // Update average response time
    const totalSuccessful = this.metrics.successfulRequests;

    if (totalSuccessful > 0) {
      this.metrics.averageResponseTime =
        (this.metrics.averageResponseTime * (totalSuccessful - 1) +
          responseTime) /
        totalSuccessful;
    }

    this.metrics.lastRequestTime = Date.now();
  }

  /**
   * Handle circuit breaker failure
   */
  private handleCircuitBreakerFailure(): void {
    this.circuitBreaker.failures++;
    this.circuitBreaker.lastFailureTime = Date.now();

    if (this.circuitBreaker.failures >= this.CIRCUIT_BREAKER_THRESHOLD) {
      this.circuitBreaker.state = 'open';
      console.warn('Circuit breaker opened due to repeated failures');
    }
  }

  /**
   * Reset circuit breaker on success
   */
  private resetCircuitBreaker(): void {
    if (this.circuitBreaker.state === 'half-open') {
      this.circuitBreaker.state = 'closed';
      this.circuitBreaker.failures = 0;
      console.info('Circuit breaker closed - service recovered');
    }
  }

  /**
   * Determine if request should be retried
   */
  private shouldRetry(error: any): boolean {
    if (error instanceof APIError) {
      // Retry on server errors and rate limits, but not on client errors
      return error.status >= 500 || error.status === 429;
    }

    return true; // Retry network errors
  }

  /**
   * Sleep utility for retry delays
   */
  private sleep(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * Batch operation to get multiple cards efficiently
   */
  async getBatchCards(cardIds: string[]): Promise<TrelloCard[]> {
    if (cardIds.length === 0) return [];

    const batches: string[][] = [];

    for (let i = 0; i < cardIds.length; i += this.MAX_BATCH_SIZE) {
      batches.push(cardIds.slice(i, i + this.MAX_BATCH_SIZE));
    }

    const batchPromises = batches.map(async (batch, index) => {
      // Add small delay between batches to respect rate limits
      if (index > 0) {
        await this.sleep(100 * index);
      }

      return Promise.all(
        batch.map(async cardId => {
          try {
            return await this.getCard(cardId);
          } catch (error) {
            console.warn(`Failed to fetch card ${cardId}:`, error);

            return null;
          }
        })
      );
    });

    const results = await Promise.all(batchPromises);

    return results.flat().filter((card): card is TrelloCard => card !== null);
  }

  /**
   * Batch operation to update multiple cards
   */
  async updateBatchCards(
    updates: Array<{ cardId: string; data: Partial<Project> }>
  ): Promise<TrelloCard[]> {
    const batches: (typeof updates)[] = [];

    for (let i = 0; i < updates.length; i += this.MAX_BATCH_SIZE) {
      batches.push(updates.slice(i, i + this.MAX_BATCH_SIZE));
    }

    const results: TrelloCard[] = [];

    for (const [index, batch] of batches.entries()) {
      // Add delay between batches
      if (index > 0) {
        await this.sleep(200 * index);
      }

      const batchResults = await Promise.allSettled(
        batch.map(({ cardId, data }) => this.updateCard(cardId, data))
      );

      batchResults.forEach((result, i) => {
        if (result.status === 'fulfilled') {
          results.push(result.value);
        } else {
          console.warn(
            `Failed to update card ${batch[i].cardId}:`,
            result.reason
          );
        }
      });
    }

    return results;
  }

  /**
   * Advanced webhook management with multiple event types
   */
  async setupAdvancedWebhooks(callbackURL: string): Promise<void> {
    // Validate callback URL
    try {
      new URL(callbackURL);
    } catch {
      throw new APIError('Invalid callback URL', 400, '/webhooks');
    }

    // Clean up existing webhooks first
    await this.cleanupExistingWebhooks();

    const webhookConfigs: WebhookConfig[] = [
      {
        modelType: 'board',
        events: ['createCard', 'updateCard', 'deleteCard', 'moveCard'],
        description: 'inPatch Suporte - Board Events',
      },
      {
        modelType: 'list',
        events: ['updateList', 'createList'],
        description: 'inPatch Suporte - List Events',
      },
      {
        modelType: 'card',
        events: [
          'commentCard',
          'addAttachmentToCard',
          'updateCheckItemStateOnCard',
        ],
        description: 'inPatch Suporte - Card Events',
      },
    ];

    for (const config of webhookConfigs) {
      try {
        const webhook = await this.createSpecificWebhook(callbackURL, config);

        this.activeWebhooks.set(webhook.id, webhook);
        console.info(
          `Created webhook for ${config.modelType} events:`,
          webhook.id
        );
      } catch (error) {
        console.warn(
          `Failed to create webhook for ${config.modelType}:`,
          error
        );
      }
    }
  }

  /**
   * Create specific webhook with configuration
   */
  private async createSpecificWebhook(
    callbackURL: string,
    config: WebhookConfig
  ): Promise<any> {
    const webhookData = {
      description:
        config.description || `inPatch Suporte - ${config.modelType} webhook`,
      callbackURL: sanitizeString(callbackURL),
      idModel: this.getConfig().trello.boardId,
      active: true,
    };

    return this.makeEnhancedRequest('/webhooks', {
      method: 'POST',
      body: JSON.stringify(webhookData),
    });
  }

  /**
   * Clean up existing webhooks to avoid duplicates
   */
  private async cleanupExistingWebhooks(): Promise<void> {
    try {
      const existingWebhooks = await this.getWebhooks();
      const inPatchWebhooks = existingWebhooks.filter(webhook =>
        webhook.description?.includes('inPatch Suporte')
      );

      for (const webhook of inPatchWebhooks) {
        try {
          await this.deleteWebhook(webhook.id);
          console.info(`Cleaned up existing webhook: ${webhook.id}`);
        } catch (error) {
          console.warn(`Failed to cleanup webhook ${webhook.id}:`, error);
        }
      }
    } catch (error) {
      console.warn('Failed to cleanup existing webhooks:', error);
    }
  }

  /**
   * Get optimized board cards with field selection
   */
  async getOptimizedBoardCards(fields?: string[]): Promise<TrelloCard[]> {
    const defaultFields = [
      'id',
      'name',
      'desc',
      'due',
      'dateLastActivity',
      'list',
      'labels',
      'members',
      'badges',
    ];

    const selectedFields = fields || defaultFields;

    let endpoint =
      `/boards/${this.getConfig().trello.boardId}/cards?` +
      `fields=${selectedFields.join(',')}&` +
      'members=true&' +
      'member_fields=fullName,username&' +
      'labels=true&' +
      'list=true&' +
      'badges=true';

    return this.makeEnhancedRequest(endpoint);
  }

  /**
   * Get board cards with intelligent caching
   */
  async getCachedBoardCards(maxAge: number = 30000): Promise<TrelloCard[]> {
    const cacheKey = 'board-cards';
    const cached = this.getFromCache(cacheKey);

    if (cached && Date.now() - cached.timestamp < maxAge) {
      console.info('Returning cached board cards');

      return cached.data;
    }

    const cards = await this.getOptimizedBoardCards();

    this.setCache(cacheKey, cards);

    return cards;
  }

  /**
   * Simple in-memory cache implementation
   */
  private cache: Map<string, { data: any; timestamp: number }> = new Map();

  private getFromCache(key: string): { data: any; timestamp: number } | null {
    return this.cache.get(key) || null;
  }

  private setCache(key: string, data: any): void {
    this.cache.set(key, { data, timestamp: Date.now() });

    // Simple cache cleanup - remove entries older than 5 minutes
    setTimeout(() => {
      const entry = this.cache.get(key);

      if (entry && Date.now() - entry.timestamp > 300000) {
        this.cache.delete(key);
      }
    }, 300000);
  }

  /**
   * Get API performance metrics
   */
  getMetrics(): APIMetrics {
    return { ...this.metrics };
  }

  /**
   * Get circuit breaker status
   */
  getCircuitBreakerStatus(): CircuitBreakerState {
    return { ...this.circuitBreaker };
  }

  /**
   * Get active webhooks
   */
  getActiveWebhooks(): Array<{ id: string; description: string }> {
    return Array.from(this.activeWebhooks.entries()).map(([id, webhook]) => ({
      id,
      description: webhook.description || 'Unknown',
    }));
  }

  /**
   * Health check method
   */
  async healthCheck(): Promise<{
    status: 'healthy' | 'degraded' | 'unhealthy';
    metrics: APIMetrics;
    circuitBreaker: CircuitBreakerState;
    lastCheck: string;
  }> {
    try {
      const startTime = Date.now();

      await this.makeEnhancedRequest('/members/me', {}, 0);
      const responseTime = Date.now() - startTime;

      let status: 'healthy' | 'degraded' | 'unhealthy' = 'healthy';

      if (this.circuitBreaker.state === 'open') {
        status = 'unhealthy';
      } else if (
        responseTime > 5000 ||
        this.metrics.failedRequests > this.metrics.successfulRequests * 0.1
      ) {
        status = 'degraded';
      }

      return {
        status,
        metrics: this.getMetrics(),
        circuitBreaker: this.getCircuitBreakerStatus(),
        lastCheck: new Date().toISOString(),
      };
    } catch (error) {
      return {
        status: 'unhealthy',
        metrics: this.getMetrics(),
        circuitBreaker: this.getCircuitBreakerStatus(),
        lastCheck: new Date().toISOString(),
      };
    }
  }

  /**
   * Reset metrics (useful for testing or periodic cleanup)
   */
  resetMetrics(): void {
    this.metrics = {
      totalRequests: 0,
      successfulRequests: 0,
      failedRequests: 0,
      averageResponseTime: 0,
      lastRequestTime: 0,
    };
  }

  /**
   * Get configuration (protected method access)
   */
  private getConfig() {
    return getAPIConfig();
  }

  /**
   * Enhanced card transformation with better error handling
   */
  transformCardsToProjectsEnhanced(cards: TrelloCard[]): Project[] {
    const validCards = cards.filter(card => {
      try {
        // More robust validation
        if (!card?.id || !card?.name) return false;
        if (typeof card.name !== 'string' || card.name.trim() === '')
          return false;

        const name = card.name.toLowerCase();

        if (
          name.includes('template') ||
          name.includes('exemplo') ||
          name.includes('test')
        ) {
          return false;
        }

        return true;
      } catch (error) {
        console.warn('Error validating card:', card?.id, error);

        return false;
      }
    });

    return validCards.map(card => {
      try {
        return this.transformCardsToProjects([card])[0];
      } catch (error) {
        console.warn('Error transforming card:', card.id, error);

        // Return a minimal valid project as fallback
        return {
          id: card.id,
          title: sanitizeString(card.name || 'Untitled'),
          description: '',
          progress: 0,
          platforms: ['Backoffice'] as Platform[],
          responsible: ['Guilherme Souza'] as TeamMember[],
          startDate: new Date().toISOString(),
          estimatedEndDate: new Date(
            Date.now() + 30 * 24 * 60 * 60 * 1000
          ).toISOString(),
          status: 'a-fazer' as const,
          priority: 'medium' as const,
          trelloCardId: card.id,
          labels: [],
          createdAt: new Date().toISOString(),
          updatedAt: new Date().toISOString(),
        };
      }
    });
  }
}

