}

interface WebhookProcessingResult {
  success: boolean;
  action: string;
  cardId?: string;
  projectsUpdated: number;
  cacheInvalidated: boolean;
  error?: string;
  processingTime: number;
}

interface WebhookStats {
  totalProcessed: number;
  successfulProcessed: number;
  failedProcessed: number;
  averageProcessingTime: number;
  eventTypes: Record<string, number>;
  lastProcessed: string;
}

/**
 * Advanced webhook handler for Trello events with intelligent processing
 * and real-time synchronization
 */
export class WebhookHandler {
  private stats: WebhookStats = {
    totalProcessed: 0,
    successfulProcessed: 0,
    failedProcessed: 0,
    averageProcessingTime: 0,
    eventTypes: {},
    lastProcessed: new Date().toISOString(),
  };

  private processingQueue: Map<string, WebhookEvent> = new Map();
  private isProcessing = false;
  private subscribers: Set<(projects: Project[]) => void> = new Set();

  /**
   * Process incoming webhook event
   */
  async processWebhookEvent(
    event: WebhookEvent
  ): Promise<WebhookProcessingResult> {
    const startTime = Date.now();
    const eventType = event.action?.type || 'unknown';

    // Update stats
    this.stats.totalProcessed++;
    this.stats.eventTypes[eventType] =
      (this.stats.eventTypes[eventType] || 0) + 1;
    this.stats.lastProcessed = new Date().toISOString();

    try {
      // Validate event
      if (!this.isValidEvent(event)) {
        throw new Error('Invalid webhook event structure');
      }

      // Add to processing queue to handle bursts
      const eventId = `${event.action.id}-${Date.now()}`;

      this.processingQueue.set(eventId, event);

      // Process the event
      const result = await this.handleEventByType(event);

      // Remove from queue
      this.processingQueue.delete(eventId);

      // Update success stats
      this.stats.successfulProcessed++;
      const processingTime = Date.now() - startTime;

      this.updateAverageProcessingTime(processingTime);

      return {
        success: true,
        action: eventType,
        cardId: event.data.card?.id,
        projectsUpdated: result.projectsUpdated,
        cacheInvalidated: result.cacheInvalidated,
        processingTime,
      };
    } catch (error) {
      this.stats.failedProcessed++;
      const processingTime = Date.now() - startTime;

      this.updateAverageProcessingTime(processingTime);

      console.error('Webhook processing error:', error);

      return {
        success: false,
        action: eventType,
        cardId: event.data.card?.id,
        projectsUpdated: 0,
        cacheInvalidated: false,
        error: error instanceof Error ? error.message : 'Unknown error',
        processingTime,
      };
    }
  }

  /**
   * Handle event based on its type
   */
  private async handleEventByType(event: WebhookEvent): Promise<{
    projectsUpdated: number;
    cacheInvalidated: boolean;
  }> {
    const eventType = event.action.type;

    switch (eventType) {
      case 'createCard':
        return this.handleCardCreated(event);

      case 'updateCard':
        return this.handleCardUpdated(event);

      case 'deleteCard':
        return this.handleCardDeleted(event);

      case 'moveCard':
        return this.handleCardMoved(event);

      case 'commentCard':
        return this.handleCardCommented(event);

      case 'addAttachmentToCard':
        return this.handleAttachmentAdded(event);

      case 'updateCheckItemStateOnCard':
        return this.handleChecklistUpdated(event);

      case 'updateList':
        return this.handleListUpdated(event);

      case 'createList':
        return this.handleListCreated(event);

      default:
        console.info(`Unhandled webhook event type: ${eventType}`);

        return { projectsUpdated: 0, cacheInvalidated: false };
    }
  }

  /**
   * Handle card creation
   */
  private async handleCardCreated(event: WebhookEvent): Promise<{
    projectsUpdated: number;
    cacheInvalidated: boolean;
  }> {
    const cardId = event.data.card?.id;

    if (!cardId) return { projectsUpdated: 0, cacheInvalidated: false };

    try {
      // Fetch the new card details
      const card = await enhancedTrelloApi.getCard(cardId);
      const projects = enhancedTrelloApi.transformCardsToProjectsEnhanced([
        card,
      ]);

      if (projects.length > 0) {
        // Invalidate cache to force refresh
        await cacheService.invalidateProjects();

        // Notify subscribers
        await this.notifySubscribers();

        return { projectsUpdated: 1, cacheInvalidated: true };
      }
    } catch (error) {
      console.warn('Failed to process card creation:', error);
    }

    return { projectsUpdated: 0, cacheInvalidated: false };
  }

  /**
   * Handle card updates
   */
  private async handleCardUpdated(event: WebhookEvent): Promise<{
    projectsUpdated: number;
    cacheInvalidated: boolean;
  }> {
    const cardId = event.data.card?.id;

    if (!cardId) return { projectsUpdated: 0, cacheInvalidated: false };

    try {
      // Check what changed
      const changes = this.detectCardChanges(event);

      if (changes.length === 0) {
        return { projectsUpdated: 0, cacheInvalidated: false };
      }

      // Fetch updated card details
      const card = await enhancedTrelloApi.getCard(cardId);
      const projects = enhancedTrelloApi.transformCardsToProjectsEnhanced([
        card,
      ]);

      if (projects.length > 0) {
        // Invalidate specific cache entries
        await cacheService.invalidateByWebhookEvent(event);

        // Notify subscribers with change details
        await this.notifySubscribers(changes);

        return { projectsUpdated: 1, cacheInvalidated: true };
      }
    } catch (error) {
      console.warn('Failed to process card update:', error);
    }

    return { projectsUpdated: 0, cacheInvalidated: false };
  }

  /**
   * Handle card deletion
   */
  private async handleCardDeleted(event: WebhookEvent): Promise<{
    projectsUpdated: number;
    cacheInvalidated: boolean;
  }> {
    const cardId = event.data.card?.id;

    if (!cardId) return { projectsUpdated: 0, cacheInvalidated: false };

    // Invalidate cache entries for deleted card
    await cacheService.invalidateByPattern(`.*${cardId}.*`);
    await cacheService.invalidateProjects();

    // Notify subscribers
    await this.notifySubscribers(['card_deleted']);

    return { projectsUpdated: 0, cacheInvalidated: true };
  }

  /**
   * Handle card moved between lists
   */
  private async handleCardMoved(event: WebhookEvent): Promise<{
    projectsUpdated: number;
    cacheInvalidated: boolean;
  }> {
    const cardId = event.data.card?.id;

    if (!cardId) return { projectsUpdated: 0, cacheInvalidated: false };

    try {
      // Fetch updated card to get new list/status
      const card = await enhancedTrelloApi.getCard(cardId);
      const projects = enhancedTrelloApi.transformCardsToProjectsEnhanced([
        card,
      ]);

      if (projects.length > 0) {
        // Invalidate cache as status likely changed
        await cacheService.invalidateProjects();

        // Notify subscribers about status change
        await this.notifySubscribers(['status_changed']);

        return { projectsUpdated: 1, cacheInvalidated: true };
      }
    } catch (error) {
      console.warn('Failed to process card move:', error);
    }

    return { projectsUpdated: 0, cacheInvalidated: false };
  }

  /**
   * Handle card comments (low priority)
   */
  private async handleCardCommented(event: WebhookEvent): Promise<{
    projectsUpdated: number;
    cacheInvalidated: boolean;
  }> {
    // Comments don't affect our project data, so minimal processing
    console.info('Card comment received:', event.data.card?.id);

    return { projectsUpdated: 0, cacheInvalidated: false };
  }

  /**
   * Handle attachment added (low priority)
   */
  private async handleAttachmentAdded(event: WebhookEvent): Promise<{
    projectsUpdated: number;
    cacheInvalidated: boolean;
  }> {
    // Attachments don't affect our project data, so minimal processing
    console.info('Card attachment added:', event.data.card?.id);

    return { projectsUpdated: 0, cacheInvalidated: false };
  }

  /**
   * Handle checklist item updates (affects progress)
   */
  private async handleChecklistUpdated(event: WebhookEvent): Promise<{
    projectsUpdated: number;
    cacheInvalidated: boolean;
  }> {
    const cardId = event.data.card?.id;

    if (!cardId) return { projectsUpdated: 0, cacheInvalidated: false };

    try {
      // Fetch updated card to recalculate progress
      const card = await enhancedTrelloApi.getCard(cardId);
      const projects = enhancedTrelloApi.transformCardsToProjectsEnhanced([
        card,
      ]);

      if (projects.length > 0) {
        // Invalidate cache as progress changed
        await cacheService.invalidateByPattern(`.*${cardId}.*`);

        // Notify subscribers about progress change
        await this.notifySubscribers(['progress_changed']);

        return { projectsUpdated: 1, cacheInvalidated: true };
      }
    } catch (error) {
      console.warn('Failed to process checklist update:', error);
    }

    return { projectsUpdated: 0, cacheInvalidated: false };
  }

  /**
   * Handle list updates
   */
  private async handleListUpdated(event: WebhookEvent): Promise<{
    projectsUpdated: number;
    cacheInvalidated: boolean;
  }> {
    // List changes might affect status mapping
    await cacheService.invalidateByPattern('trello-board-lists');

    // Might need to refresh all projects if list names changed
    await cacheService.invalidateProjects();

    await this.notifySubscribers(['list_updated']);

    return { projectsUpdated: 0, cacheInvalidated: true };
  }

  /**
   * Handle list creation
   */
  private async handleListCreated(event: WebhookEvent): Promise<{
    projectsUpdated: number;
    cacheInvalidated: boolean;
  }> {
    // New list created, invalidate lists cache
    await cacheService.invalidateByPattern('trello-board-lists');

    await this.notifySubscribers(['list_created']);

    return { projectsUpdated: 0, cacheInvalidated: true };
  }

  /**
   * Detect what changed in a card update
   */
  private detectCardChanges(event: WebhookEvent): string[] {
    const changes: string[] = [];
    const old = event.data.old;
    const current = event.data.card;

    if (!old || !current) return changes;

    if (old.name !== current.name) changes.push('title_changed');
    if (old.desc !== current.desc) changes.push('description_changed');
    if (old.due !== current.due) changes.push('due_date_changed');
    if (old.closed !== current.closed) changes.push('archived_changed');
    if (old.pos !== undefined) changes.push('position_changed');

    return changes;
  }

  /**
   * Validate webhook event structure
   */
  private isValidEvent(event: WebhookEvent): boolean {
    return !!(
      event &&
      event.action &&
      event.action.type &&
      event.action.id &&
      event.data
    );
  }

  /**
   * Update average processing time
   */
  private updateAverageProcessingTime(newTime: number): void {
    const totalProcessed =
      this.stats.successfulProcessed + this.stats.failedProcessed;

    if (totalProcessed === 1) {
      this.stats.averageProcessingTime = newTime;
    } else {
      this.stats.averageProcessingTime =
        (this.stats.averageProcessingTime * (totalProcessed - 1) + newTime) /
        totalProcessed;
    }
  }

  /**
   * Subscribe to webhook events
   */
  subscribe(callback: (projects: Project[]) => void): () => void {
    this.subscribers.add(callback);

    // Return unsubscribe function
    return () => {
      this.subscribers.delete(callback);
    };
  }

  /**
   * Notify all subscribers
   */
  private async notifySubscribers(changes?: string[]): Promise<void> {
    if (this.subscribers.size === 0) return;

    try {
      // Fetch fresh projects data
      const cards = await enhancedTrelloApi.getCachedBoardCards(10000); // 10 second cache
      const projects =
        enhancedTrelloApi.transformCardsToProjectsEnhanced(cards);

      // Notify all subscribers
      for (const callback of this.subscribers) {
        try {
          callback(projects);
        } catch (error) {
          console.warn('Error notifying webhook subscriber:', error);
        }
      }
    } catch (error) {
      console.warn('Failed to fetch projects for webhook notification:', error);
    }
  }

  /**
   * Process queued events (for handling bursts)
   */
  async processQueue(): Promise<void> {
    if (this.isProcessing || this.processingQueue.size === 0) return;

    this.isProcessing = true;

    try {
      const events = Array.from(this.processingQueue.values());
      const batchSize = 5;

      for (let i = 0; i < events.length; i += batchSize) {
        const batch = events.slice(i, i + batchSize);

        await Promise.allSettled(
          batch.map(event => this.processWebhookEvent(event))
        );

        // Small delay between batches
        if (i + batchSize < events.length) {
          await new Promise(resolve => setTimeout(resolve, 100));
        }
      }
    } finally {
      this.isProcessing = false;
    }
  }

  /**
   * Get webhook processing statistics
   */
  getStats(): WebhookStats {
    return { ...this.stats };
  }

  /**
   * Reset statistics
   */
  resetStats(): void {
    this.stats = {
      totalProcessed: 0,
      successfulProcessed: 0,
      failedProcessed: 0,
      averageProcessingTime: 0,
      eventTypes: {},
      lastProcessed: new Date().toISOString(),
    };
  }

  /**
   * Get health status
   */
  getHealthStatus(): {
    status: 'healthy' | 'degraded' | 'unhealthy';
    queueSize: number;
    successRate: number;
    averageProcessingTime: number;
    lastProcessed: string;
  } {
    const successRate =
      this.stats.totalProcessed > 0
        ? (this.stats.successfulProcessed / this.stats.totalProcessed) * 100
        : 100;

    let status: 'healthy' | 'degraded' | 'unhealthy' = 'healthy';

    if (successRate < 50 || this.processingQueue.size > 50) {
      status = 'unhealthy';
    } else if (
      successRate < 80 ||
      this.processingQueue.size > 20 ||
      this.stats.averageProcessingTime > 5000
    ) {
      status = 'degraded';
    }

    return {
      status,
      queueSize: this.processingQueue.size,
      successRate,
      averageProcessingTime: this.stats.averageProcessingTime,
      lastProcessed: this.stats.lastProcessed,
    };
  }
}

// Export singleton instance
export const webhookHandler = new WebhookHandler();

import { Project } from '@/types/project';

interface CacheEntry<T> {
  data: T;
  timestamp: number;
  ttl: number;
  hits: number;
  lastAccessed: number;
}

interface CacheStats {
  totalEntries: number;
  totalHits: number;
  totalMisses: number;
  hitRate: number;
  memoryUsage: number;
  oldestEntry: number;
  newestEntry: number;
}

interface CacheConfig {
  maxSize: number;
  defaultTTL: number;
  cleanupInterval: number;
  enableStats: boolean;
}

/**
 * Intelligent cache service with TTL, LRU eviction, and automatic cleanup
 * Optimized for Trello API responses and project data
 */
export class CacheService {
  private cache: Map<string, CacheEntry<any>> = new Map();
  private stats = {
    hits: 0,
    misses: 0,
    evictions: 0,
    cleanups: 0,
  };

  private config: CacheConfig = {
    maxSize: 1000,
    defaultTTL: 300000, // 5 minutes
    cleanupInterval: 60000, // 1 minute
    enableStats: true,
  };

  private cleanupTimer: NodeJS.Timeout | null = null;

  constructor(config?: Partial<CacheConfig>) {
    this.config = { ...this.config, ...config };
    this.startCleanupTimer();
  }

  /**
   * Get cached projects with intelligent freshness check
   */
  async getCachedProjects(maxAge?: number): Promise<Project[] | null> {
    const key = 'trello-projects';
    const ttl = maxAge || this.config.defaultTTL;

    return this.get<Project[]>(key, ttl);
  }

  /**
   * Set cached projects with metadata
   */
  async setCachedProjects(projects: Project[], ttl?: number): Promise<void> {
    const key = 'trello-projects';
    const cacheTime = ttl || this.config.defaultTTL;

    this.set(key, projects, cacheTime);

    // Also cache individual projects for quick lookup
    projects.forEach(project => {
      if (project.id) {
        this.set(`project-${project.id}`, project, cacheTime);
      }
      if (project.trelloCardId) {
        this.set(`trello-card-${project.trelloCardId}`, project, cacheTime);
      }
    });
  }

  /**
   * Get cached project by ID
   */
  async getCachedProject(projectId: string): Promise<Project | null> {
    return this.get<Project>(`project-${projectId}`);
  }

  /**
   * Get cached project by Trello card ID
   */
  async getCachedProjectByTrelloId(
    trelloCardId: string
  ): Promise<Project | null> {
    return this.get<Project>(`trello-card-${trelloCardId}`);
  }

  /**
   * Cache Trello board lists
   */
  async setCachedBoardLists(lists: any[], ttl?: number): Promise<void> {
    const cacheTime = ttl || this.config.defaultTTL;

    this.set('trello-board-lists', lists, cacheTime);
  }

  /**
   * Get cached board lists
   */
  async getCachedBoardLists(): Promise<any[] | null> {
    return this.get<any[]>('trello-board-lists');
  }

  /**
   * Cache board actions for change detection
   */
  async setCachedBoardActions(actions: any[], ttl?: number): Promise<void> {
    const cacheTime = ttl || this.config.defaultTTL / 2; // Shorter TTL for actions

    this.set('trello-board-actions', actions, cacheTime);
  }

  /**
   * Get cached board actions
   */
  async getCachedBoardActions(): Promise<any[] | null> {
    return this.get<any[]>('trello-board-actions');
  }

  /**
   * Generic get method with TTL check
   */
  private get<T>(key: string, maxAge?: number): T | null {
    const entry = this.cache.get(key);

    if (!entry) {
      this.stats.misses++;

      return null;
    }

    const now = Date.now();
    const age = now - entry.timestamp;
    const ttl = maxAge || entry.ttl;

    if (age > ttl) {
      this.cache.delete(key);
      this.stats.misses++;

      return null;
    }

    // Update access statistics
    entry.hits++;
    entry.lastAccessed = now;
    this.stats.hits++;

    return entry.data as T;
  }

  /**
   * Generic set method with TTL and size management
   */
  private set<T>(key: string, data: T, ttl?: number): void {
    const now = Date.now();
    const cacheTime = ttl || this.config.defaultTTL;

    // Check if we need to evict entries
    if (this.cache.size >= this.config.maxSize) {
      this.evictLRU();
    }

    const entry: CacheEntry<T> = {
      data,
      timestamp: now,
      ttl: cacheTime,
      hits: 0,
      lastAccessed: now,
    };

    this.cache.set(key, entry);
  }

  /**
   * Evict least recently used entry
   */
  private evictLRU(): void {
    let oldestKey = '';
    let oldestTime = Date.now();

    for (const [key, entry] of this.cache.entries()) {
      if (entry.lastAccessed < oldestTime) {
        oldestTime = entry.lastAccessed;
        oldestKey = key;
      }
    }

    if (oldestKey) {
      this.cache.delete(oldestKey);
      this.stats.evictions++;
    }
  }

  /**
   * Invalidate specific cache entries
   */
  async invalidateProjects(): Promise<void> {
    const keysToDelete: string[] = [];

    for (const key of this.cache.keys()) {
      if (
        key.startsWith('trello-projects') ||
        key.startsWith('project-') ||
        key.startsWith('trello-card-')
      ) {
        keysToDelete.push(key);
      }
    }

    keysToDelete.forEach(key => this.cache.delete(key));
  }

  /**
   * Invalidate all cache entries
   */
  async invalidateAll(): Promise<void> {
    this.cache.clear();
  }

  /**
   * Invalidate entries by pattern
   */
  async invalidateByPattern(pattern: string): Promise<void> {
    const keysToDelete: string[] = [];
    const regex = new RegExp(pattern);

    for (const key of this.cache.keys()) {
      if (regex.test(key)) {
        keysToDelete.push(key);
      }
    }

    keysToDelete.forEach(key => this.cache.delete(key));
  }

  /**
   * Smart invalidation based on Trello webhook events
   */
  async invalidateByWebhookEvent(event: any): Promise<void> {
    const { action, data } = event;

    switch (action?.type) {
      case 'createCard':
      case 'updateCard':
      case 'deleteCard':
        // Invalidate projects cache and specific card cache
        await this.invalidateProjects();
        if (data?.card?.id) {
          this.cache.delete(`trello-card-${data.card.id}`);
        }
        break;

      case 'moveCard':
        // Invalidate projects cache as status might have changed
        await this.invalidateProjects();
        break;

      case 'updateList':
      case 'createList':
        // Invalidate board lists cache
        this.cache.delete('trello-board-lists');
        break;

      case 'commentCard':
      case 'addAttachmentToCard':
        // These don't affect our project data, so no invalidation needed
        break;

      default:
        // For unknown events, invalidate projects to be safe
        await this.invalidateProjects();
    }
  }

  /**
   * Preload cache with fresh data
   */
  async preloadCache(
    projects: Project[],
    lists?: any[],
    actions?: any[]
  ): Promise<void> {
    await this.setCachedProjects(projects);

    if (lists) {
      await this.setCachedBoardLists(lists);
    }

    if (actions) {
      await this.setCachedBoardActions(actions);
    }
  }

  /**
   * Get cache statistics
   */
  getStats(): CacheStats {
    const entries = Array.from(this.cache.values());
    const now = Date.now();

    const totalHits = this.stats.hits;
    const totalMisses = this.stats.misses;
    const totalRequests = totalHits + totalMisses;

    return {
      totalEntries: this.cache.size,
      totalHits,
      totalMisses,
      hitRate: totalRequests > 0 ? (totalHits / totalRequests) * 100 : 0,
      memoryUsage: this.estimateMemoryUsage(),
      oldestEntry:
        entries.length > 0 ? Math.min(...entries.map(e => e.timestamp)) : now,
      newestEntry:
        entries.length > 0 ? Math.max(...entries.map(e => e.timestamp)) : now,
    };
  }

  /**
   * Estimate memory usage (rough calculation)
   */
  private estimateMemoryUsage(): number {
    let size = 0;

    for (const [key, entry] of this.cache.entries()) {
      // Rough estimation: key size + JSON string size of data
      size += key.length * 2; // UTF-16 characters
      size += JSON.stringify(entry.data).length * 2;
      size += 64; // Overhead for entry metadata
    }

    return size;
  }

  /**
   * Start automatic cleanup timer
   */
  private startCleanupTimer(): void {
    this.cleanupTimer = setInterval(() => {
      this.cleanup();
    }, this.config.cleanupInterval);
  }

  /**
   * Cleanup expired entries
   */
  private cleanup(): void {
    const now = Date.now();
    const keysToDelete: string[] = [];

    for (const [key, entry] of this.cache.entries()) {
      const age = now - entry.timestamp;

      if (age > entry.ttl) {
        keysToDelete.push(key);
      }
    }

    keysToDelete.forEach(key => this.cache.delete(key));

    if (keysToDelete.length > 0) {
      this.stats.cleanups++;
    }
  }

  /**
   * Stop cleanup timer and clear cache
   */
  destroy(): void {
    if (this.cleanupTimer) {
      clearInterval(this.cleanupTimer);
      this.cleanupTimer = null;
    }
    this.cache.clear();
  }

  /**
   * Check if cache is healthy
   */
  isHealthy(): boolean {
    const stats = this.getStats();

    // Consider cache healthy if:
    // - Hit rate is above 50% (if we have enough requests)
    // - Memory usage is reasonable
    // - Not too many entries
    const totalRequests = stats.totalHits + stats.totalMisses;

    if (totalRequests < 10) return true; // Not enough data to judge

    return (
      stats.hitRate > 50 &&
      stats.memoryUsage < 50 * 1024 * 1024 && // 50MB
      stats.totalEntries < this.config.maxSize * 0.9
    );
  }

  /**
   * Get cache configuration
   */
  getConfig(): CacheConfig {
    return { ...this.config };
  }

  /**
   * Update cache configuration
   */
  updateConfig(newConfig: Partial<CacheConfig>): void {
    this.config = { ...this.config, ...newConfig };

    // Restart cleanup timer if interval changed
    if (newConfig.cleanupInterval && this.cleanupTimer) {
      clearInterval(this.cleanupTimer);
      this.startCleanupTimer();
    }
  }
}

// Export singleton instance
export const cacheService = new CacheService({
  maxSize: 500,
  defaultTTL: 300000, // 5 minutes
  cleanupInterval: 60000, // 1 minute
  enableStats: true,
});

// API Configuration with security best practices
export interface APIConfig {
  trello: {
    apiKey: string | null;
    apiToken: string | null;
    boardId: string;
    baseUrl: string;
  };
  supabase: {
    url: string | null;
    anonKey: string | null;
  };
}

// Validate environment variables
function validateEnvVar(
  name: string,
  value: string | undefined
): string | null {
  if (!value || value.trim() === '') {
    // Environment variable not set - handle silently in production
    if (process.env.NODE_ENV === 'development') {
      console.warn(`Environment variable ${name} is not set or empty`);
    }

    return null;
  }

  return value.trim();
}

// Get API configuration with validation
export function getAPIConfig(): APIConfig {
  return {
    trello: {
      apiKey: validateEnvVar(
        'NEXT_PUBLIC_TRELLO_API_KEY',
        process.env.NEXT_PUBLIC_TRELLO_API_KEY
      ),
      apiToken: validateEnvVar(
        'NEXT_PUBLIC_TRELLO_API_TOKEN',
        process.env.NEXT_PUBLIC_TRELLO_API_TOKEN
      ),
      boardId: process.env.NEXT_PUBLIC_TRELLO_BOARD_ID || 'RVFcbKeF',
      baseUrl: 'https://api.trello.com/1',
    },
    supabase: {
      url: validateEnvVar(
        'NEXT_PUBLIC_SUPABASE_URL',
        process.env.NEXT_PUBLIC_SUPABASE_URL
      ),
      anonKey: validateEnvVar(
        'NEXT_PUBLIC_SUPABASE_ANON_KEY',
        process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY
      ),
    },
  };
}

// Check if Trello is properly configured
export function isTrelloConfigured(): boolean {
  const config = getAPIConfig();

  return !!(config.trello.apiKey && config.trello.apiToken);
}

// Check if Supabase is properly configured
export function isSupabaseConfigured(): boolean {
  const config = getAPIConfig();

  return !!(config.supabase.url && config.supabase.anonKey);
}

