export const supabaseMCPService = new SupabaseMCPService();

import toast from 'react-hot-toast';

import { trelloApi } from '@/lib/trello';
import { projectsApi } from '@/lib/supabase';
import { Project } from '@/types/project';

export class TrelloSyncService {
  private static instance: TrelloSyncService;
  private syncInProgress = false;
  private isPollingActive = false;

  static getInstance(): TrelloSyncService {
    if (!TrelloSyncService.instance) {
      TrelloSyncService.instance = new TrelloSyncService();
    }

    return TrelloSyncService.instance;
  }

  async syncFromTrello(): Promise<Project[]> {
    if (this.syncInProgress) {
      toast.error('Sincroniza√ß√£o j√° em andamento');
      throw new Error('Sync already in progress');
    }

    this.syncInProgress = true;

    try {
      toast.loading('Sincronizando com Trello...', { id: 'trello-sync' });

      // Fetch cards from Trello
      const trelloCards = await trelloApi.getBoardCards();

      // Transform to Project format
      const projects = trelloApi.transformCardsToProjects(trelloCards);

      // Filter out cards without proper content (empty or template cards)
      const validProjects = projects.filter(
        project =>
          project.title &&
          project.title.trim() !== '' &&
          !project.title.toLowerCase().includes('template') &&
          !project.title.toLowerCase().includes('exemplo')
      );

      // Try to sync with Supabase (if available) or return projects directly
      let syncedProjects: Project[];

      try {
        syncedProjects = await projectsApi.syncFromTrello(trelloCards);
        toast.success(
          `${syncedProjects.length} projetos sincronizados com Supabase!`,
          { id: 'trello-sync' }
        );
      } catch (supabaseError) {
        // If Supabase is not available, return Trello data directly
        // Supabase not available, using Trello data directly
        syncedProjects = validProjects;
        toast.success(
          `${validProjects.length} projetos carregados do Trello!`,
          { id: 'trello-sync' }
        );
      }

      return syncedProjects;
    } catch (error) {
      const message =
        error instanceof Error ? error.message : 'Erro desconhecido';

      toast.error(`Erro ao sincronizar: ${message}`, { id: 'trello-sync' });
      throw error;
    } finally {
      this.syncInProgress = false;
    }
  }

  async createProjectInTrello(project: Omit<Project, 'id'>): Promise<void> {
    try {
      toast.loading('Criando projeto no Trello...', { id: 'create-trello' });

      // Get board lists to find appropriate list
      const lists = await trelloApi.getBoardLists();
      const planningList = lists.find(
        list =>
          list.name.toLowerCase().includes('planning') ||
          list.name.toLowerCase().includes('backlog')
      );

      if (!planningList) {
        throw new Error('Lista de planejamento n√£o encontrada no Trello');
      }

      // Create card in Trello
      await trelloApi.createCard(planningList.id, project);

      toast.success('Projeto criado no Trello!', { id: 'create-trello' });
    } catch (error) {
      const message =
        error instanceof Error ? error.message : 'Erro desconhecido';

      toast.error(`Erro ao criar projeto: ${message}`, { id: 'create-trello' });
      throw error;
    }
  }

  async updateProjectInTrello(
    projectId: string,
    updates: Partial<Project>
  ): Promise<void> {
    try {
      toast.loading('Atualizando projeto no Trello...', {
        id: 'update-trello',
      });

      await trelloApi.updateCard(projectId, updates);

      toast.success('Projeto atualizado no Trello!', { id: 'update-trello' });
    } catch (error) {
      const message =
        error instanceof Error ? error.message : 'Erro desconhecido';

      toast.error(`Erro ao atualizar projeto: ${message}`, {
        id: 'update-trello',
      });
      throw error;
    }
  }

  isSyncInProgress(): boolean {
    return this.syncInProgress;
  }

  startRealTimeSync(onUpdate: (projects: Project[]) => void): void {
    if (this.isPollingActive) {
      // Real-time sync already active

      return;
    }

    // Starting real-time sync with Trello
    this.isPollingActive = true;

    trelloApi.startPolling(30000, projects => {
      // Real-time sync update received
      onUpdate(projects);
    });
  }

  stopRealTimeSync(): void {
    if (!this.isPollingActive) {
      return;
    }

    // Stopping real-time sync
    this.isPollingActive = false;
    trelloApi.stopPolling();
  }

  isRealTimeSyncActive(): boolean {
    return this.isPollingActive;
  }

  async setupWebhook(callbackURL: string): Promise<void> {
    try {
      // Check existing webhooks
      const webhooks = await trelloApi.getWebhooks();
      const existingWebhook = webhooks.find(w => w.callbackURL === callbackURL);

      if (existingWebhook) {
        // Webhook already exists
        return;
      }

      // Create new webhook
      const webhook = await trelloApi.createWebhook(callbackURL);

      // Sync completed successfully
      toast.success('Webhook configurado com sucesso!');
    } catch (error) {
      // Log errors only in development
      if (process.env.NODE_ENV === 'development') {
        console.error('Failed to setup webhook:', error);
      }
      toast.error('Erro ao configurar webhook');
      throw error;
    }
  }
}

export const trelloSyncService = TrelloSyncService.getInstance();

/**
 * WebhookHandler - Processamento de webhooks Trello no lado cliente
 * Integra√ß√£o com MCP e real-time subscriptions
 * Seguindo padr√µes de Vibe Coding e arquitetura Next.js 15
 */

import type { Project as TrelloProject } from '@/types/project';

import { databaseService } from '@/lib/database/prisma';
import { supabaseMCPService } from '@/lib/services/supabase-mcp';

/**
 * Payload do webhook Trello processado
 */
export interface WebhookPayload {
  action: {
    type: string;
    data: {
      card?: {
        id: string;
        name: string;
        desc: string;
        due: string | null;
        list: { name: string };
        labels: Array<{ name: string; color: string }>;
        members: Array<{ fullName: string }>;
        checklists?: Array<{
          checkItems: Array<{ state: 'complete' | 'incomplete' }>;
        }>;
      };
    };
  };
}

/**
 * Resultado do processamento do webhook
 */
export interface WebhookProcessResult {
  success: boolean;
  projectId?: string;
  action?: string;
  error?: string;
  details?: any;
}

export class WebhookHandler {
  private isProcessing = false;
  private processingQueue: WebhookPayload[] = [];

  /**
   * Processa webhook do Trello
   */
  async processWebhook(payload: WebhookPayload): Promise<WebhookProcessResult> {
    try {
      // Adiciona √† fila se j√° est√° processando
      if (this.isProcessing) {
        this.processingQueue.push(payload);

        return { success: true, action: 'queued' };
      }

      this.isProcessing = true;

      const result = await this.handleWebhookPayload(payload);

      // Processa fila se houver itens pendentes
      await this.processQueue();

      return result;
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Erro desconhecido',
      };
    } finally {
      this.isProcessing = false;
    }
  }

  /**
   * Processa payload individual do webhook
   */
  private async handleWebhookPayload(
    payload: WebhookPayload
  ): Promise<WebhookProcessResult> {
    const { action } = payload;

    // Eventos relevantes para sincroniza√ß√£o
    const relevantEvents = [
      'createCard',
      'updateCard',
      'deleteCard',
      'moveCardToBoard',
      'moveCardFromBoard',
    ];

    if (!relevantEvents.includes(action.type)) {
      return { success: true, action: 'ignored' };
    }

    if (!action.data.card) {
      return { success: true, action: 'no-card-data' };
    }

    try {
      // Transforma dados do Trello para formato do projeto
      const trelloProject = this.transformWebhookToProject(payload);

      if (!trelloProject) {
        return { success: false, error: 'Falha na transforma√ß√£o dos dados' };
      }

      // Sincroniza com banco via DatabaseService
      const syncResult = await databaseService.syncFromTrelloWithMCP([
        trelloProject,
      ]);

      if (syncResult.errors.length > 0) {
        // Erros na sincroniza√ß√£o - log silencioso
      }

      // Notifica subscriptions em tempo real se MCP estiver ativo
      await this.notifyRealtimeSubscriptions(trelloProject, action.type);

      return {
        success: true,
        projectId: trelloProject.trelloCardId,
        action: action.type,
        details: {
          syncSuccess: syncResult.success,
          syncErrors: syncResult.errors.length,
        },
      };
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Erro no processamento',
      };
    }
  }

  /**
   * Transforma dados do webhook em projeto Trello
   */
  private transformWebhookToProject(
    payload: WebhookPayload
  ): TrelloProject | null {
    const card = payload.action.data.card;

    if (!card) return null;

    try {
      // Mapeamento de status
      const statusMapping: Record<string, string> = {
        planning: 'a-fazer',
        backlog: 'a-fazer',
        'to do': 'a-fazer',
        fazer: 'a-fazer',
        development: 'em-andamento',
        doing: 'em-andamento',
        progress: 'em-andamento',
        andamento: 'em-andamento',
        desenvolvimento: 'em-andamento',
        done: 'concluido',
        completed: 'concluido',
        concluido: 'concluido',
        finalizado: 'concluido',
      };

      // Mapeamento de plataformas
      const platformMapping: Record<string, string> = {
        n8n: 'N8N',
        jira: 'Jira',
        hubspot: 'Hubspot',
        backoffice: 'Backoffice',
        'google workspace': 'Google Workspace',
        google: 'Google Workspace',
      };

      // Mapeamento de membros
      const memberMapping: Record<string, string> = {
        'guilherme souza': 'Guilherme Souza',
        guilherme: 'Guilherme Souza',
        'felipe braat': 'Felipe Braat',
        felipe: 'Felipe Braat',
        'tiago triani': 'Tiago Triani',
        tiago: 'Tiago Triani',
      };

      // Extrai plataformas dos labels
      const platforms = card.labels
        .map(label => {
          const labelName = label.name.toLowerCase();

          return platformMapping[labelName] || null;
        })
        .filter(Boolean);

      // Extrai respons√°veis dos membros
      const responsible = card.members.map(member => {
        const memberName = member.fullName.toLowerCase();

        return memberMapping[memberName] || member.fullName;
      });

      // Calcula progresso baseado em checklists
      let progress = 0;

      if (card.checklists && card.checklists.length > 0) {
        const allItems = card.checklists.flatMap(
          checklist => checklist.checkItems
        );
        const completedItems = allItems.filter(
          item => item.state === 'complete'
        );

        progress =
          allItems.length > 0
            ? Math.round((completedItems.length / allItems.length) * 100)
            : 0;
      } else {
        // Progresso baseado no status se n√£o h√° checklists
        const listName = card.list.name.toLowerCase();

        if (statusMapping[listName] === 'a-fazer') progress = 5;
        else if (statusMapping[listName] === 'em-andamento') progress = 45;
        else if (statusMapping[listName] === 'concluido') progress = 100;
      }

      // Determina prioridade baseada em labels
      let priority = 'medium';
      const hasRedLabel = card.labels.some(label => label.color === 'red');
      const hasYellowLabel = card.labels.some(
        label => label.color === 'yellow'
      );
      const hasGreenLabel = card.labels.some(label => label.color === 'green');

      if (hasRedLabel) priority = 'high';
      else if (hasGreenLabel) priority = 'low';
      else if (hasYellowLabel) priority = 'medium';

      const trelloProject: TrelloProject = {
        id: card.id,
        title: this.sanitizeString(card.name),
        description: this.sanitizeString(card.desc || ''),
        progress,
        platforms: platforms.length > 0 ? (platforms as any[]) : ['Backoffice'],
        responsible:
          responsible.length > 0 ? (responsible as any[]) : ['Guilherme Souza'],
        status: (statusMapping[card.list.name.toLowerCase()] ||
          'a-fazer') as any,
        priority: priority as any,
        trelloCardId: card.id,
        labels: card.labels.map(label => label.name).filter(Boolean),
        startDate: new Date().toISOString(),
        estimatedEndDate:
          card.due ||
          new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString(),
        imageUrl: undefined,
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString(),
      };

      return trelloProject;
    } catch (error) {
      return null;
    }
  }

  /**
   * Notifica subscriptions em tempo real
   */
  private async notifyRealtimeSubscriptions(
    project: TrelloProject,
    actionType: string
  ): Promise<void> {
    try {
      const mcpStatus = supabaseMCPService.getConnectionStatus();

      if (!mcpStatus.connected) {
        return;
      }

      // Configura subscription se n√£o existir
      if (mcpStatus.subscriptions === 0) {
        await supabaseMCPService.setupRealtimeSubscription({
          table: 'projects',
          event: '*',
          callback: payload => {
            // Aqui seria disparado evento para UI
            if (typeof window !== 'undefined') {
              window.dispatchEvent(
                new CustomEvent('project-updated', {
                  detail: { payload, actionType },
                })
              );
            }
          },
        });
      }

      console.log(
        `üì° Notifica√ß√£o real-time enviada: ${project.title} (${actionType})`
      );
    } catch (error) {
      console.error('Erro ao notificar subscriptions:', error);
    }
  }

  /**
   * Processa fila de webhooks pendentes
   */
  private async processQueue(): Promise<void> {
    while (this.processingQueue.length > 0) {
      const payload = this.processingQueue.shift();

      if (payload) {
        await this.handleWebhookPayload(payload);
      }
    }
  }

  /**
   * Sanitiza strings para seguran√ßa
   */
  private sanitizeString(input: string): string {
    if (!input || typeof input !== 'string') return '';

    return input
      .trim()
      .replace(/[<>]/g, '') // Remove HTML tags
      .replace(/javascript:/gi, '') // Remove JS protocol
      .replace(/on\w+=/gi, '') // Remove event handlers
      .substring(0, 255); // Limita tamanho
  }

  /**
   * Obt√©m estat√≠sticas da fila de processamento
   */
  getQueueStats() {
    return {
      isProcessing: this.isProcessing,
      queueLength: this.processingQueue.length,
    };
  }

  /**
   * Limpa fila de processamento
   */
  clearQueue(): void {
    this.processingQueue = [];
  }
}

// Inst√¢ncia singleton do handler de webhooks
export const webhookHandler = new WebhookHandler();

/**
 * Edge Function para processar webhooks do Trello
 * Integra√ß√£o com MCP para sincroniza√ß√£o em tempo real
 * Seguindo padr√µes de Vibe Coding e seguran√ßa enterprise
 */

// @ts-ignore - Deno runtime imports
import { serve } from 'https://deno.land/std@0.168.0/http/server.ts';
// @ts-ignore - Deno runtime imports
import { createClient } from 'https://esm.sh/@supabase/supabase-js@2';

// Tipos para webhook do Trello
interface TrelloWebhookPayload {
  action: {
    type: string;
    data: {
      card?: {
        id: string;
        name: string;
        desc: string;
        due: string | null;
        list: {
          id: string;
          name: string;
        };
        labels: Array<{
          id: string;
          name: string;
          color: string;
        }>;
        members: Array<{
          id: string;
          fullName: string;
        }>;
        checklists?: Array<{
          checkItems: Array<{
            state: 'complete' | 'incomplete';
          }>;
        }>;
      };
      list?: {
        id: string;
        name: string;
      };
      board?: {
        id: string;
        name: string;
      };
    };
  };
  model: {
    id: string;
    name: string;
  };
}

interface ProcessedProject {
  title: string;
  description: string;
  progress: number;
  platforms: string[];
  responsible: string[];
  status: string;
  priority: string;
  trelloCardId: string;
  labels: string[];
  estimatedEndDate: string;
}

/**
 * Processa webhook do Trello e transforma em projeto
 */
function processWebhookData(
  payload: TrelloWebhookPayload
): ProcessedProject | null {
  const { action } = payload;

  // S√≥ processa eventos de cards
  if (!action.data.card) {
    // Webhook ignorado: n√£o √© evento de card
    return null;
  }

  const card = action.data.card;

  try {
    // Mapeamento de status baseado na lista
    const statusMapping: Record<string, string> = {
      planning: 'a-fazer',
      backlog: 'a-fazer',
      'to do': 'a-fazer',
      fazer: 'a-fazer',
      development: 'em-andamento',
      doing: 'em-andamento',
      progress: 'em-andamento',
      andamento: 'em-andamento',
      desenvolvimento: 'em-andamento',
      done: 'concluido',
      completed: 'concluido',
      concluido: 'concluido',
      finalizado: 'concluido',
    };

    // Mapeamento de plataformas
    const platformMapping: Record<string, string> = {
      n8n: 'N8N',
      jira: 'Jira',
      hubspot: 'Hubspot',
      backoffice: 'Backoffice',
      'google workspace': 'Google Workspace',
      google: 'Google Workspace',
    };

    // Mapeamento de membros
    const memberMapping: Record<string, string> = {
      'guilherme souza': 'Guilherme Souza',
      guilherme: 'Guilherme Souza',
      'felipe braat': 'Felipe Braat',
      felipe: 'Felipe Braat',
      'tiago triani': 'Tiago Triani',
      tiago: 'Tiago Triani',
    };

    // Extrai plataformas dos labels
    const platforms = card.labels
      .map(label => {
        const labelName = label.name.toLowerCase();

        return platformMapping[labelName];
      })
      .filter((platform): platform is string => Boolean(platform));

    // Extrai respons√°veis dos membros
    const responsible = card.members.map(member => {
      const memberName = member.fullName.toLowerCase();

      return memberMapping[memberName] || member.fullName;
    });

    // Calcula progresso baseado em checklists
    let progress = 0;

    if (card.checklists && card.checklists.length > 0) {
      const allItems = card.checklists.flatMap(
        checklist => checklist.checkItems
      );
      const completedItems = allItems.filter(item => item.state === 'complete');

      progress =
        allItems.length > 0
          ? Math.round((completedItems.length / allItems.length) * 100)
          : 0;
    } else {
      // Progresso baseado no status se n√£o h√° checklists
      const listName = card.list.name.toLowerCase();

      if (statusMapping[listName] === 'a-fazer') progress = 5;
      else if (statusMapping[listName] === 'em-andamento') progress = 45;
      else if (statusMapping[listName] === 'concluido') progress = 100;
    }

    // Determina prioridade baseada em labels vermelhos/amarelos
    let priority = 'medium';
    const hasRedLabel = card.labels.some(label => label.color === 'red');
    const hasYellowLabel = card.labels.some(label => label.color === 'yellow');
    const hasGreenLabel = card.labels.some(label => label.color === 'green');

    if (hasRedLabel) priority = 'high';
    else if (hasGreenLabel) priority = 'low';
    else if (hasYellowLabel) priority = 'medium';

    const processedProject: ProcessedProject = {
      title: sanitizeString(card.name),
      description: sanitizeString(card.desc || ''),
      progress,
      platforms: platforms.length > 0 ? platforms : ['Backoffice'],
      responsible: responsible.length > 0 ? responsible : ['Guilherme Souza'],
      status: statusMapping[card.list.name.toLowerCase()] || 'a-fazer',
      priority,
      trelloCardId: card.id,
      labels: card.labels.map(label => label.name).filter(Boolean),
      estimatedEndDate:
        card.due ||
        new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString(),
    };

    return processedProject;
  } catch (error) {
    // Log error silently in production
    if ((globalThis as any).Deno?.env?.get?.('NODE_ENV') === 'development') {
      console.error('Erro ao processar dados do webhook:', error);
    }

    return null;
  }
}

/**
 * Sanitiza strings para seguran√ßa
 */
function sanitizeString(input: string): string {
  if (!input || typeof input !== 'string') return '';

  return input
    .trim()
    .replace(/[<>]/g, '') // Remove HTML tags
    .replace(/javascript:/gi, '') // Remove JS protocol
    .replace(/on\w+=/gi, '') // Remove event handlers
    .substring(0, 255); // Limita tamanho
}

/**
 * Valida assinatura do webhook (seguran√ßa)
 */
function validateWebhookSignature(request: Request, _body: string): boolean {
  const signature = request.headers.get('x-trello-webhook');

  // Access Deno environment variables properly
  const secret = (globalThis as any).Deno?.env?.get?.('TRELLO_WEBHOOK_SECRET');

  if (!signature || !secret) {
    // Webhook sem assinatura ou secret n√£o configurado
    return false;
  }

  // Implementar valida√ß√£o HMAC aqui se necess√°rio
  // Por enquanto, aceita qualquer webhook com header
  return true;
}

/**
 * Handler principal da Edge Function
 */
serve(async (req: Request) => {
  // CORS headers
  const corsHeaders = {
    'Access-Control-Allow-Origin': '*',
    'Access-Control-Allow-Headers':
      'authorization, x-client-info, apikey, content-type',
    'Access-Control-Allow-Methods': 'POST, OPTIONS',
  };

  // Handle CORS preflight
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  // S√≥ aceita POST
  if (req.method !== 'POST') {
    return new Response('Method not allowed', {
      status: 405,
      headers: corsHeaders,
    });
  }

  try {
    const body = await req.text();

    // Valida assinatura do webhook
    if (!validateWebhookSignature(req, body)) {
      return new Response('Unauthorized', {
        status: 401,
        headers: corsHeaders,
      });
    }

    const payload: TrelloWebhookPayload = JSON.parse(body);

    // Processa apenas eventos relevantes
    const relevantEvents = [
      'createCard',
      'updateCard',
      'deleteCard',
      'moveCardToBoard',
      'moveCardFromBoard',
    ];

    if (!relevantEvents.includes(payload.action.type)) {
      return new Response('Event ignored', {
        status: 200,
        headers: corsHeaders,
      });
    }

    // Processa dados do webhook
    const processedProject = processWebhookData(payload);

    if (!processedProject) {
      return new Response('No project data', {
        status: 200,
        headers: corsHeaders,
      });
    }

    // Conecta ao Supabase
    const supabaseUrl = (globalThis as any).Deno?.env?.get?.('SUPABASE_URL')!;
    const supabaseKey = (globalThis as any).Deno?.env?.get?.(
      'SUPABASE_SERVICE_ROLE_KEY'
    )!;
    const supabase = createClient(supabaseUrl, supabaseKey);

    // Upsert do projeto no banco
    const { data, error } = await supabase.from('projects').upsert(
      {
        title: processedProject.title,
        description: processedProject.description,
        progress: processedProject.progress,
        platforms: processedProject.platforms,
        responsible: processedProject.responsible,
        status: processedProject.status,
        priority: processedProject.priority,
        trello_card_id: processedProject.trelloCardId,
        labels: processedProject.labels,
        estimated_end_date: processedProject.estimatedEndDate,
        start_date: new Date().toISOString(),
        updated_at: new Date().toISOString(),
      },
      {
        onConflict: 'trello_card_id',
      }
    );

    if (error) {
      return new Response('Database error', {
        status: 500,
        headers: corsHeaders,
      });
    }

    // Log de sucesso no hist√≥rico de sync
    await supabase.from('sync_history').insert({
      project_id: data?.[0]?.id || 'unknown',
      action: payload.action.type === 'createCard' ? 'CREATED' : 'UPDATED',
      source: 'trello-webhook',
      success: true,
      details: {
        webhookType: payload.action.type,
        cardId: processedProject.trelloCardId,
        timestamp: new Date().toISOString(),
      },
    });

    // Projeto sincronizado via webhook silentemente

    return new Response(
      JSON.stringify({
        success: true,
        project: processedProject.title,
        action: payload.action.type,
      }),
      {
        status: 200,
        headers: {
          ...corsHeaders,
          'Content-Type': 'application/json',
        },
      }
    );
  } catch (error) {
    return new Response(
      JSON.stringify({
        success: false,
        error: 'Internal server error',
      }),
      {
        status: 500,
        headers: {
          ...corsHeaders,
          'Content-Type': 'application/json',
        },
      }
    );
  }
});
